import React, { useState, useEffect } from 'react';

const CaptchaGate = ({ onVerified }) => {
  const [captcha, setCaptcha] = useState({ question: '', answer: '' });
  const [userAnswer, setUserAnswer] = useState('');
  const [error, setError] = useState('');
  const [attempts, setAttempts] = useState(0);

  const generateCaptcha = () => {
    const operations = ['+', '-', '*'];
    const operation = operations[Math.floor(Math.random() * operations.length)];
    let num1, num2, answer;

    if (operation === '+') {
      num1 = Math.floor(Math.random() * 10) + 1;
      num2 = Math.floor(Math.random() * 10) + 1;
      answer = num1 + num2;
    } else if (operation === '-') {
      num1 = Math.floor(Math.random() * 10) + 6;
      num2 = Math.floor(Math.random() * 5) + 1;
      answer = num1 - num2;
    } else {
      num1 = Math.floor(Math.random() * 5) + 1;
      num2 = Math.floor(Math.random() * 5) + 1;
      answer = num1 * num2;
    }

    setCaptcha({ question: `${num1} ${operation} ${num2}`, answer: answer.toString() });
    setUserAnswer('');
    setError('');
  };

  useEffect(() => {
    generateCaptcha();
  }, []);

  const handleSubmit = (e) => {
    e.preventDefault();

    if (!userAnswer.trim()) {
      setError('Please enter the CAPTCHA answer.');
      return;
    }

    if (userAnswer.trim() === captcha.answer) {
      sessionStorage.setItem('captchaVerified', 'true');
      onVerified();
    } else {
      setAttempts(attempts + 1);
      setError('Your answer is wrong. Please try again.');
      generateCaptcha();
    }
  };

  return (
    <div className="captcha-container">
      <h2>Human Verification</h2>
      <p>Please solve this CAPTCHA to proceed:</p>
      
      <div className="captcha-box">
        <strong>{captcha.question} = ?</strong>
        <input
          type="text"
          value={userAnswer}
          onChange={(e) => setUserAnswer(e.target.value)}
          placeholder="Enter answer"
        />
        {error && <p className="error-message">{error}</p>}
      </div>

      <button type="button" onClick={generateCaptcha}>
        Refresh CAPTCHA
      </button>
      <button type="submit" onClick={handleSubmit} disabled={attempts >= 3}>
        Continue
      </button>

      {attempts >= 3 && <p className="error-message">Too many failed attempts. Please refresh the page.</p>}
    </div>
  );
};

export default CaptchaGate;
