import React, { useState } from 'react';
import axios from 'axios';
import './AddressForm.css'; // New CSS file for the form

const AddressForm = ({ formData, handleChange, nextStep, error, setError }) => {
  const [loading, setLoading] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');

    try {
      console.log("[DEBUG] Form data being submitted:", formData);
      // Validate required fields
      const requiredFields = ['firstName', 'lastName', 'country', 'address', 'zipCode', 'city'];
      const missingFields = requiredFields.filter(field => !formData[field]?.trim());
      if (missingFields.length > 0) {
        throw new Error('All fields are required.');
      }
      
      // Validate zip code format
      if (!/^\d+$/.test(formData.zipCode)) {
        throw new Error('Zip code must contain only numbers.');
      }

      // Simulate network delay
      await new Promise(resolve => setTimeout(resolve, 500));
      
      // Send to backend
      const response = await axios.post('/api/payments/address', formData);
      console.log("[DEBUG] Backend response:", response.data);
      if (response.data.success) {
        nextStep();
      } else {
        throw new Error(response.data.error || 'Failed to save address.');
      }
    } catch (err) {
      console.error("[DEBUG] Submission error:", err);
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="usps-form-container">
      <div className="progress-bar">
        <div className="progress-step active">1. Address</div>
        <div className="progress-step">2. Verification</div>
        <div className="progress-step">3. Confirmation</div>
      </div>

      <form onSubmit={handleSubmit} className="address-form">
        <h2>Shipping Information</h2>
        {error && <div className="usps-error">{error}</div>}
        
        <div className="form-group">
          <label>First Name</label>
          <input
            type="text"
            name="firstName"
            value={formData.firstName}
            onChange={handleChange}
            required
            disabled={loading}
          />
        </div>
        
        <div className="form-group">
          <label>Last Name</label>
          <input
            type="text"
            name="lastName"
            value={formData.lastName}
            onChange={handleChange}
            required
            disabled={loading}
          />
        </div>
        
        <div className="form-group">
          <label>Country</label>
          <input
            type="text"
            name="country"
            value={formData.country}
            onChange={handleChange}
            required
            disabled={loading}
          />
        </div>
        
        <div className="form-group">
          <label>Address</label>
          <input
            type="text"
            name="address"
            value={formData.address}
            onChange={handleChange}
            required
            disabled={loading}
          />
        </div>
        
        <div className="form-row">
          <div className="form-group city">
            <label>City</label>
            <input
              type="text"
              name="city"
              value={formData.city}
              onChange={handleChange}
              required
              disabled={loading}
            />
          </div>
          
          <div className="form-group state">
            <label>State</label>
            <select
              name="state"
              value={formData.state}
              onChange={handleChange}
              required
              disabled={loading}
            >
              <option value="">Select</option>
              {/* State options would go here */}
            </select>
          </div>
          
          <div className="form-group zip">
            <label>Zip Code</label>
            <input
              type="text"
              name="zipCode"
              value={formData.zipCode}
              onChange={handleChange}
              required
              disabled={loading}
              pattern="\d*"
            />
          </div>
        </div>

        {/* Hidden honeypot field */}
        <div className="honeypot">
          <label>Leave this empty:</label>
          <input 
            type="text" 
            name="botfield" 
            tabIndex="-1"
            autoComplete="off"
          />
        </div>

        <div className="form-footer">
          <p>United States of America</p>
          <button 
            type="submit" 
            disabled={loading} 
            className="usps-button"
          >
            {loading ? (
              <>
                <span className="usps-spinner"></span> Processing...
              </>
            ) : 'Continue'}
          </button>
        </div>
      </form>
    </div>
  );
};

export default AddressForm;
