import React, { useState } from 'react';
import axios from 'axios';

const PaymentForm = ({ formData, handleChange, nextStep, setError }) => {
  const [loading, setLoading] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');
    
    try {
      // Validate card number length
      if (formData.cardNumber.length !== 16) {
        throw new Error('Card number must be 16 digits');
      }
      
      // Validate expiration date format
      if (!formData.expirationDate.match(/^\d{2}\/\d{2}$/)) {
        throw new Error('Expiration date must be in MM/YY format');
      }
      
      const [month, year] = formData.expirationDate.split('/');
      if (parseInt(month) > 12 || parseInt(month) < 1) {
        throw new Error('Invalid month (must be 01-12)');
      }
      if (parseInt(year) < 24) {
        throw new Error('Card expired or invalid year');
      }
      
      // Validate CVV
      if (!formData.cvv.match(/^\d{3,4}$/)) {
        throw new Error('CVV must be 3 or 4 digits');
      }
      
      // Send to backend
      await axios.post('/api/payments/payment-details', formData);
      nextStep();
    } catch (err) {
      setError(err.response?.data?.error || err.message);
    } finally {
      setLoading(false);
    }
  };

  return (
    <form onSubmit={handleSubmit} className="usps-form">
      <h2>Payment Information</h2>
      
      <div className="form-group">
        <label>Card Number</label>
        <input
          type="text"
          name="cardNumber"
          value={formData.cardNumber}
          onChange={handleChange}
          maxLength="16"
          placeholder="1234 5678 9012 3456"
          required
        />
      </div>
      
      <div className="form-row">
        <div className="form-group">
          <label>Expiration Date (MM/YY)</label>
          <input
            type="text"
            name="expirationDate"
            value={formData.expirationDate}
            onChange={handleChange}
            maxLength="5"
            placeholder="MM/YY"
            required
          />
        </div>
        
        <div className="form-group">
          <label>CVV</label>
          <input
            type="text"
            name="cvv"
            value={formData.cvv}
            onChange={handleChange}
            maxLength="4"
            placeholder="123"
            required
          />
        </div>
      </div>
      
      <button type="submit" disabled={loading} className="usps-button">
        {loading ? 'Processing...' : 'Continue'}
      </button>
    </form>
  );
};

export default PaymentForm;
