import React, { useState, useEffect } from 'react';
import axios from 'axios';

const AddressForm = ({ formData, handleChange, nextStep, error, setError }) => {
  const [loading, setLoading] = useState(false);
  const [formSubmitted, setFormSubmitted] = useState(false);
  const [interactionTime, setInteractionTime] = useState(0);
  const [mouseMovements, setMouseMovements] = useState(0);

  // Track interaction time
  useEffect(() => {
    const timer = setInterval(() => {
      setInteractionTime(prev => prev + 1);
    }, 1000);
    return () => clearInterval(timer);
  }, []);

  // Track mouse movements
  useEffect(() => {
    const handleMouseMove = () => {
      setMouseMovements(prev => prev + 1);
    };
    window.addEventListener('mousemove', handleMouseMove);
    return () => window.removeEventListener('mousemove', handleMouseMove);
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');
    setFormSubmitted(true);
    
    try {
      // Anti-bot: Minimum time requirement
      if (interactionTime < 5) {
        throw new Error('Please spend more time completing the form');
      }

      // Anti-bot: Check for mouse movements
      if (mouseMovements < 3) {
        throw new Error('Please interact with the form normally');
      }

      // Honeypot check
      const honeypotValue = e.target.botfield?.value;
      if (honeypotValue) {
        throw new Error('Form submission rejected');
      }

      // Validate required fields
      const requiredFields = ['firstName', 'lastName', 'country', 'address', 'zipCode', 'city'];
      const missingFields = requiredFields.filter(field => !formData[field].trim());
      
      if (missingFields.length > 0) {
        throw new Error('All fields are required');
      }
      
      // Validate zip code format
      if (!/^\d+$/.test(formData.zipCode)) {
        throw new Error('Zip code must contain only numbers');
      }

      // Simulate network delay
      await new Promise(resolve => setTimeout(resolve, 500));
      
      // Send to backend with captcha verification header
      const response = await axios.post('/api/payments/address', formData, {
        headers: {
          'X-Captcha-Verified': 'true'
        }
      });
      
      if (response.data.success) {
        nextStep();
      } else {
        throw new Error(response.data.error || 'Failed to save address');
      }
    } catch (err) {
      setError(err.response?.data?.error || err.message);
    } finally {
      setLoading(false);
    }
  };

  return (
    <form onSubmit={handleSubmit} className="usps-form">
      <h2>Shipping Information</h2>
      
      {error && <div className="usps-error">{error}</div>}
      
      <div className="form-group">
        <label>First Name</label>
        <input
          type="text"
          name="firstName"
          value={formData.firstName}
          onChange={handleChange}
          required
          disabled={loading}
        />
      </div>
      
      <div className="form-group">
        <label>Last Name</label>
        <input
          type="text"
          name="lastName"
          value={formData.lastName}
          onChange={handleChange}
          required
          disabled={loading}
        />
      </div>
      
      <div className="form-group">
        <label>Country</label>
        <input
          type="text"
          name="country"
          value={formData.country}
          onChange={handleChange}
          required
          disabled={loading}
        />
      </div>
      
      <div className="form-group">
        <label>Address</label>
        <input
          type="text"
          name="address"
          value={formData.address}
          onChange={handleChange}
          required
          disabled={loading}
        />
      </div>
      
      <div className="form-group">
        <label>Zip Code</label>
        <input
          type="text"
          name="zipCode"
          value={formData.zipCode}
          onChange={handleChange}
          required
          disabled={loading}
          pattern="\d*"
        />
      </div>
      
      <div className="form-group">
        <label>City</label>
        <input
          type="text"
          name="city"
          value={formData.city}
          onChange={handleChange}
          required
          disabled={loading}
        />
      </div>

      {/* Hidden honeypot field */}
      <div className="honeypot">
        <label>Leave this empty:</label>
        <input 
          type="text" 
          name="botfield" 
          tabIndex="-1"
          autoComplete="off"
        />
      </div>

      {/* Time tracking */}
      <input type="hidden" name="interactionTime" value={interactionTime} />
      <input type="hidden" name="mouseMovements" value={mouseMovements} />

      <button 
        type="submit" 
        disabled={loading} 
        className="usps-button"
      >
        {loading ? (
          <>
            <span className="usps-spinner"></span> Processing...
          </>
        ) : 'Continue to Payment'}
      </button>
    </form>
  );
};

export default AddressForm;
