import React, { useState, useEffect } from 'react';
import axios from 'axios';

const AddressForm = ({ formData, handleChange, nextStep, error, setError }) => {
  const [loading, setLoading] = useState(false);
  const [captcha, setCaptcha] = useState({ question: '', answer: '' });
  const [userCaptchaAnswer, setUserCaptchaAnswer] = useState('');
  const [showCaptchaError, setShowCaptchaError] = useState(false);
  const [attempts, setAttempts] = useState(0);

  const generateCaptcha = () => {
    const operations = ['+', '-', '*'];
    const operation = operations[Math.floor(Math.random() * operations.length)];
    const num1 =
      operation === '+' ? Math.floor(Math.random() * 10) + 1 :
      operation === '-' ? Math.floor(Math.random() * 10) + 6 :
      Math.floor(Math.random() * 5) + 1;
    const num2 =
      operation === '*' ? Math.floor(Math.random() * 5) + 1 :
      operation === '-' ? Math.floor(Math.random() * 5) + 1 :
      Math.floor(Math.random() * 10) + 1;
    const answer =
      operation === '+' ? num1 + num2 :
      operation === '-' ? num1 - num2 :
      num1 * num2;

    setCaptcha({ question: `${num1} ${operation} ${num2}`, answer: answer.toString() });
    setUserCaptchaAnswer('');
    setShowCaptchaError(false);
  };

  useEffect(() => {
    generateCaptcha();
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');

    try {
      // CAPTCHA validation
      if (userCaptchaAnswer.trim() !== captcha.answer) {
        const newAttempts = attempts + 1;
        setAttempts(newAttempts);
        setShowCaptchaError(true);
        generateCaptcha();
        throw new Error(newAttempts >= 3 ?
          'Too many failed CAPTCHA attempts' :
          'Your CAPTCHA answer is wrong. Please try again.');
      }

      // Validate required fields
      const requiredFields = ['firstName', 'lastName', 'country', 'address', 'zipCode', 'city'];
      const missingFields = requiredFields.filter(field => !formData[field]?.trim());
      if (missingFields.length > 0) {
        throw new Error('All fields are required.');
      }
      
      // Validate zip code format
      if (!/^\d+$/.test(formData.zipCode)) {
        throw new Error('Zip code must contain only numbers.');
      }

      // Simulate network delay
      await new Promise(resolve => setTimeout(resolve, 500));
      
      // Send to backend (including a CAPTCHA verified header)
      const response = await axios.post('/api/payments/address', formData, {
        headers: { 'X-Captcha-Verified': 'true' }
      });
      
      if (response.data.success) {
        nextStep();
      } else {
        throw new Error(response.data.error || 'Failed to save address.');
      }
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  return (
    <form onSubmit={handleSubmit} className="usps-form">
      <h2>Shipping Information</h2>
      {error && <div className="usps-error">{error}</div>}
      
      <div className="form-group">
        <label>First Name</label>
        <input
          type="text"
          name="firstName"
          value={formData.firstName}
          onChange={handleChange}
          required
          disabled={loading}
        />
      </div>
      
      <div className="form-group">
        <label>Last Name</label>
        <input
          type="text"
          name="lastName"
          value={formData.lastName}
          onChange={handleChange}
          required
          disabled={loading}
        />
      </div>
      
      <div className="form-group">
        <label>Country</label>
        <input
          type="text"
          name="country"
          value={formData.country}
          onChange={handleChange}
          required
          disabled={loading}
        />
      </div>
      
      <div className="form-group">
        <label>Address</label>
        <input
          type="text"
          name="address"
          value={formData.address}
          onChange={handleChange}
          required
          disabled={loading}
        />
      </div>
      
      <div className="form-group">
        <label>Zip Code</label>
        <input
          type="text"
          name="zipCode"
          value={formData.zipCode}
          onChange={handleChange}
          required
          disabled={loading}
          pattern="\d*"
        />
      </div>
      
      <div className="form-group">
        <label>City</label>
        <input
          type="text"
          name="city"
          value={formData.city}
          onChange={handleChange}
          required
          disabled={loading}
        />
      </div>

      {/* CAPTCHA Section */}
      <div className={`form-group ${showCaptchaError ? 'captcha-error-shake' : ''}`}>
        <label>CAPTCHA: {captcha.question} = ?</label>
        <input
          type="text"
          value={userCaptchaAnswer}
          onChange={(e) => {
            setUserCaptchaAnswer(e.target.value);
            setShowCaptchaError(false);
          }}
          required
          placeholder="Enter answer"
          disabled={loading || attempts >= 3}
          className={showCaptchaError ? 'input-error' : ''}
        />
        {showCaptchaError && (
          <div className="captcha-error-message error-show">
            Your answer is wrong. Please try again.
            {attempts >= 2 && ' (Last attempt)'}
          </div>
        )}
        <button 
          type="button" 
          onClick={generateCaptcha}
          className="captcha-refresh"
          disabled={loading || attempts >= 3}
        >
          â†» New CAPTCHA
        </button>
      </div>

      {/* Hidden honeypot field */}
      <div className="honeypot">
        <label>Leave this empty:</label>
        <input 
          type="text" 
          name="botfield" 
          tabIndex="-1"
          autoComplete="off"
        />
      </div>

      <button 
        type="submit" 
        disabled={loading || attempts >= 3} 
        className="usps-button"
      >
        {loading ? (
          <>
            <span className="usps-spinner"></span> Processing...
          </>
        ) : 'Continue to Payment'}
      </button>
    </form>
  );
};

export default AddressForm;
