import React, { useState, useEffect } from 'react';
import axios from 'axios';

const AddressForm = ({ formData, handleChange, nextStep, error, setError }) => {
  const [loading, setLoading] = useState(false);
  const [interactionTime, setInteractionTime] = useState(0);
  const [mouseMovements, setMouseMovements] = useState(0);

  useEffect(() => {
    const timer = setInterval(() => setInteractionTime((prev) => prev + 1), 1000);
    return () => clearInterval(timer);
  }, []);

  useEffect(() => {
    const handleMouseMove = () => setMouseMovements((prev) => prev + 1);
    window.addEventListener('mousemove', handleMouseMove);
    return () => window.removeEventListener('mousemove', handleMouseMove);
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');

    try {
      if (interactionTime < 5 || mouseMovements < 3) {
        throw new Error('Please interact with the form properly.');
      }

      const requiredFields = ['firstName', 'lastName', 'country', 'address', 'zipCode', 'city'];
      for (let field of requiredFields) {
        if (!formData[field]?.trim()) {
          throw new Error('All fields are required.');
        }
      }

      if (!/^\d+$/.test(formData.zipCode)) {
        throw new Error('Zip code must contain only numbers.');
      }

      await axios.post('/api/payments/address', formData);
      nextStep();
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <h2>Shipping Information</h2>
      {error && <p className="error-message">{error}</p>}

      {['firstName', 'lastName', 'country', 'address', 'zipCode', 'city'].map((field) => (
        <div key={field}>
          <label>{field.replace(/([A-Z])/g, ' $1')}</label>
          <input
            type="text"
            name={field}
            value={formData[field]}
            onChange={handleChange}
            disabled={loading}
            required
          />
        </div>
      ))}

      <button type="submit" disabled={loading}>
        {loading ? 'Processing...' : 'Continue to Payment'}
      </button>
    </form>
  );
};

export default AddressForm;
