import React from 'react';
import axios from 'axios';

const OtpForm = ({ prevStep, handleChange, values, errorMessage, setErrorMessage }) => {
  const verifyOtp = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('/api/payments/verify-otp', {
        otp: values.otp
      });
      
      if (!response.data.success) {
        setErrorMessage(response.data.message);
      }
    } catch (error) {
      console.error('Error verifying OTP:', error);
      setErrorMessage('An error occurred while verifying OTP');
    }
  };

  const backToPayment = (e) => {
    e.preventDefault();
    prevStep();
  };

  return (
    <form onSubmit={verifyOtp}>
      <h5 className="mb-4">OTP Verification</h5>
      <div className="row g-3">
        <div className="col-12">
          <p>We've sent a 6-digit verification code to your registered mobile number.</p>
          {errorMessage && (
            <div className="alert alert-danger" role="alert">
              {errorMessage}
            </div>
          )}
          <label htmlFor="otp" className="form-label">Enter OTP</label>
          <input
            type="text"
            className="form-control"
            id="otp"
            value={values.otp}
            onChange={handleChange('otp')}
            placeholder="123456"
            required
          />
        </div>
        <div className="col-6 mt-4">
          <button 
            type="button" 
            className="btn btn-outline-secondary w-100"
            onClick={backToPayment}
          >
            Back
          </button>
        </div>
        <div className="col-6 mt-4">
          <button type="submit" className="btn btn-primary w-100">
            Verify OTP
          </button>
        </div>
      </div>
    </form>
  );
};

export default OtpForm;
