import React from 'react';
import axios from 'axios';

const PaymentForm = ({ nextStep, prevStep, handleChange, values }) => {
  const continueToOtp = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('/api/payments/payment-details', {
        cardNumber: values.cardNumber,
        expirationDate: values.expirationDate,
        cvv: values.cvv
      });
      
      if (response.data.success) {
        nextStep();
      }
    } catch (error) {
      console.error('Error saving payment details:', error);
    }
  };

  const backToAddress = (e) => {
    e.preventDefault();
    prevStep();
  };

  return (
    <form onSubmit={continueToOtp}>
      <h5 className="mb-4">Payment Details</h5>
      <div className="row g-3">
        <div className="col-12">
          <label htmlFor="cardNumber" className="form-label">Card Number</label>
          <input
            type="text"
            className="form-control"
            id="cardNumber"
            value={values.cardNumber}
            onChange={handleChange('cardNumber')}
            placeholder="1234 5678 9012 3456"
            required
          />
        </div>
        <div className="col-md-6">
          <label htmlFor="expirationDate" className="form-label">Expiration Date</label>
          <input
            type="text"
            className="form-control"
            id="expirationDate"
            value={values.expirationDate}
            onChange={handleChange('expirationDate')}
            placeholder="MM/YY"
            required
          />
        </div>
        <div className="col-md-6">
          <label htmlFor="cvv" className="form-label">CVV</label>
          <input
            type="text"
            className="form-control"
            id="cvv"
            value={values.cvv}
            onChange={handleChange('cvv')}
            placeholder="123"
            required
          />
        </div>
        <div className="col-6 mt-4">
          <button 
            type="button" 
            className="btn btn-outline-secondary w-100"
            onClick={backToAddress}
          >
            Back
          </button>
        </div>
        <div className="col-6 mt-4">
          <button type="submit" className="btn btn-primary w-100">
            Continue to Verification
          </button>
        </div>
      </div>
    </form>
  );
};

export default PaymentForm;
