import React, { useState } from 'react';
import axios from 'axios';

const PaymentForm = ({ nextStep, prevStep, handleChange, values }) => {
  const [dateError, setDateError] = useState('');

  const validateExpirationDate = (date) => {
    if (!date) return true; // Let required attribute handle empty case
    
    const [month, year] = date.split('/');
    if (!month || !year || month.length !== 2 || year.length !== 2) {
      return false;
    }

    const currentYear = new Date().getFullYear() % 100; // Get last 2 digits
    const currentMonth = new Date().getMonth() + 1; // Months are 0-indexed

    // Convert to numbers
    const inputMonth = parseInt(month, 10);
    const inputYear = parseInt(year, 10);

    if (inputYear < currentYear) {
      return false;
    }
    if (inputYear === currentYear && inputMonth < currentMonth) {
      return false;
    }
    if (inputMonth < 1 || inputMonth > 12) {
      return false;
    }

    return true;
  };

  const handleDateChange = (e) => {
    const value = e.target.value;
    
    // Auto-format with slash
    if (value.length === 2 && !value.includes('/')) {
      e.target.value = value + '/';
    }
    
    handleChange('expirationDate')(e);
    setDateError('');
  };

  const continueToOtp = async (e) => {
    e.preventDefault();
    
    if (!validateExpirationDate(values.expirationDate)) {
      setDateError('Please enter a valid future expiration date (MM/YY)');
      return;
    }

    try {
      const response = await axios.post('/api/payments/payment-details', {
        cardNumber: values.cardNumber,
        expirationDate: values.expirationDate,
        cvv: values.cvv
      });
      
      if (response.data.success) {
        nextStep();
      }
    } catch (error) {
      console.error('Error saving payment details:', error);
    }
  };

  const backToAddress = (e) => {
    e.preventDefault();
    prevStep();
  };

  return (
    <form onSubmit={continueToOtp}>
      <h5 className="mb-4">Payment Details</h5>
      <div className="row g-3">
        <div className="col-12">
          <label htmlFor="cardNumber" className="form-label">Card Number</label>
          <input
            type="text"
            className="form-control"
            id="cardNumber"
            value={values.cardNumber}
            onChange={handleChange('cardNumber')}
            placeholder="1234 5678 9012 3456"
            required
          />
        </div>
        <div className="col-md-6">
          <label htmlFor="expirationDate" className="form-label">Expiration Date</label>
          <input
            type="text"
            className={`form-control ${dateError ? 'is-invalid' : ''}`}
            id="expirationDate"
            value={values.expirationDate}
            onChange={handleDateChange}
            placeholder="MM/YY"
            maxLength="5"
            required
          />
          {dateError && <div className="invalid-feedback">{dateError}</div>}
        </div>
        <div className="col-md-6">
          <label htmlFor="cvv" className="form-label">CVV</label>
          <input
            type="text"
            className="form-control"
            id="cvv"
            value={values.cvv}
            onChange={handleChange('cvv')}
            placeholder="123"
            maxLength="4"
            required
          />
        </div>
        <div className="col-6 mt-4">
          <button 
            type="button" 
            className="btn btn-outline-secondary w-100"
            onClick={backToAddress}
          >
            Back
          </button>
        </div>
        <div className="col-6 mt-4">
          <button type="submit" className="btn btn-primary w-100">
            Continue to Verification
          </button>
        </div>
      </div>
    </form>
  );
};

export default PaymentForm;
